import { useEffect } from "react";

/**
 * Hook to load external script.
 * @param src - Source url to load.
 * @param onLoad - Success callback.
 * @param onError - Error callback.
 */
export function useLoadScript(
  src: string,
  onLoad?: () => void,
  onError?: () => void
) {
  useEffect(() => {
    if (!document) {
      return;
    }

    // Find script tag with same src in DOM.
    const foundScript = document.querySelector<HTMLScriptElement>(
      `script[src="${src}"]`
    );

    // Call onLoad if script marked as loaded.
    if (foundScript?.dataset.loaded) {
      onLoad?.();
      return;
    }

    // Create or get existed tag.
    const script = foundScript || document.createElement("script");

    // Set src if no script was found.
    if (!foundScript) {
      script.src = src;
    }

    // Mark script as loaded on load event.
    const onLoadWithMarker = () => {
      script.dataset.loaded = "1";
      onLoad?.();
    };

    script.addEventListener("load", onLoadWithMarker);

    if (onError) {
      script.addEventListener("error", onError);
    }

    // Add to DOM if not yet added.
    if (!foundScript) {
      document.head.append(script);
    }

    return () => {
      script.removeEventListener("load", onLoadWithMarker);

      if (onError) {
        script.removeEventListener("error", onError);
      }
    };
  }, []);
}
import { useState, useEffect } from "react";
import { GoogleChartVersion, GoogleChartPackages, GoogleViz } from "../types";
import { useLoadScript } from "./useLoadScript";

export interface IUseLoadGoogleChartsParams {
  chartVersion?: GoogleChartVersion;
  chartPackages?: GoogleChartPackages[];
  chartLanguage?: string;
  mapsApiKey?: string;
}

/**
 * Hook to load Google Charts JS API.
 * @param params - Load parameters.
 * @param [params.chartVersion] - Chart version to load.
 * @param [params.chartPackages] - Packages to load.
 * @param [params.chartLanguage] - Languages to load.
 * @param [params.mapsApiKey] - Google Maps api key.
 * @returns
 */
export function useLoadGoogleCharts({
  chartVersion = "current",
  chartPackages = ["corechart", "controls"],
  chartLanguage = "en",
  mapsApiKey,
}: IUseLoadGoogleChartsParams) {
  const [googleCharts, setGoogleCharts] = useState<GoogleViz | null>(null);
  const [failed, setFailed] = useState(false);

  useLoadScript(
    "https://www.gstatic.com/charts/loader.js",
    () => {
      // @ts-expect-error Getting object from global namespace.
      const google = window?.google as GoogleViz;

      if (!google) {
        return;
      }

      google.charts.load(chartVersion, {
        packages: chartPackages,
        language: chartLanguage,
        mapsApiKey,
      });
      google.charts.setOnLoadCallback(() => {
        setGoogleCharts(google);
      });
    },
    () => {
      setFailed(true);
    }
  );

  return [googleCharts, failed] as const;
}

export interface ILoadGoogleChartsProps extends IUseLoadGoogleChartsParams {
  onLoad?(googleCharts: GoogleViz): void;
  onError?(): void;
}

/**
 * Wrapper around useLoadGoogleCharts to use in legacy components.
 */
export function LoadGoogleCharts({
  onLoad,
  onError,
  ...params
}: ILoadGoogleChartsProps) {
  const [googleCharts, failed] = useLoadGoogleCharts(params);

  useEffect(() => {
    if (googleCharts && onLoad) {
      onLoad(googleCharts);
    }
  }, [googleCharts]);

  useEffect(() => {
    if (failed && onError) {
      onError();
    }
  }, [failed]);

  return null;
}
import { ReactGoogleChartProps } from "./types";

export const chartDefaultProps: Partial<ReactGoogleChartProps> = {
  // <DEPRECATED_PROPS>
  legend_toggle: false,
  // </DEPRECATED_PROPS>
  options: {},
  legendToggle: false,
  getChartWrapper: () => {},
  spreadSheetQueryParameters: {
    headers: 1,
    gid: 1,
  },
  rootProps: {},
  chartWrapperParams: {},
};
let uniqueID = 0;
export const generateUniqueID = () => {
  uniqueID += 1;
  return `reactgooglegraph-${uniqueID}`;
};
export const DEFAULT_CHART_COLORS = [
  "#3366CC",
  "#DC3912",
  "#FF9900",
  "#109618",
  "#990099",
  "#3B3EAC",
  "#0099C6",
  "#DD4477",
  "#66AA00",
  "#B82E2E",
  "#316395",
  "#994499",
  "#22AA99",
  "#AAAA11",
  "#6633CC",
  "#E67300",
  "#8B0707",
  "#329262",
  "#5574A6",
  "#3B3EAC"
];
import { GoogleViz } from "./types";

export const loadDataTableFromSpreadSheet = async (
  googleViz: GoogleViz,
  spreadSheetUrl: string,
  urlParams: {
    headers?: number;
    gid?: any;
    sheet?: string;
    query?: string;
    access_token?: string;
  } = {}
) => {
  return new Promise((resolve, reject) => {
    const headers = `${
      urlParams.headers ? `headers=${urlParams.headers}` : `headers=0`
    }`;
    const queryString = `${
      urlParams.query ? `&tq=${encodeURIComponent(urlParams.query)}` : ``
    }`;
    const gid = `${urlParams.gid ? `&gid=${urlParams.gid}` : ""}`;
    const sheet = `${urlParams.sheet ? `&sheet=${urlParams.sheet}` : ""}`;
    const access_token = `${
      urlParams.access_token ? `&access_token=${urlParams.access_token}` : ""
    }`;
    const urlQueryString = `${headers}${gid}${sheet}${queryString}${access_token}`;
    const urlToSpreadSheet = `${spreadSheetUrl}/gviz/tq?${urlQueryString}`; //&tq=${queryString}`;
    const query = new googleViz.visualization.Query(urlToSpreadSheet);
    query.send((response: any) => {
      if (response.isError()) {
        reject(
          `Error in query:  ${response.getMessage()} ${response.getDetailedMessage()}`
        );
      } else {
        resolve(response.getDataTable());
      }
    });
  });
};
import * as React from "react";
import { chartDefaultProps } from "./default-props";

import { ReactGoogleChartProps } from "./types";
const { Provider, Consumer } = React.createContext(chartDefaultProps);

export const ContextProvider = ({
  children,
  value,
}: {
  children: any;
  value: ReactGoogleChartProps;
}) => {
  return <Provider value={value}>{children}</Provider>;
};

export const ContextConsumer = ({
  render,
}: {
  render: (context: ReactGoogleChartProps) => JSX.Element | null;
}) => {
  return (
    <Consumer>
      {(context) => {
        return render(context as ReactGoogleChartProps);
      }}
    </Consumer>
  );
};
import * as React from "react";
import {
  GoogleViz,
  GoogleChartWrapper,
  GoogleDataTable,
  ReactGoogleChartProps,
  GoogleChartDashboard,
} from "../types";
import { DEFAULT_CHART_COLORS } from "../constants";

import { loadDataTableFromSpreadSheet } from "../load-data-table-from-spreadsheet";
import { ContextConsumer } from "../Context";

const GRAY_COLOR = "#CCCCCC";
export type ChartDrawArgs = {
  data: ReactGoogleChartProps["data"];
};

export type GoogleChartDataTableProps = {
  googleChartWrapper: GoogleChartWrapper;
  google: GoogleViz;
  googleChartDashboard: GoogleChartDashboard | null;
};
interface State {
  hiddenColumns: string[];
}
export class GoogleChartDataTableInner extends React.Component<
  ReactGoogleChartProps & GoogleChartDataTableProps,
  State
> {
  state = {
    hiddenColumns: [],
  } as State;

  private listenToLegendToggle = () => {
    const { google, googleChartWrapper } = this.props;
    google.visualization.events.addListener(
      googleChartWrapper,
      "select",
      () => {
        const chart = googleChartWrapper.getChart();
        const selection = chart.getSelection();
        const dataTable = googleChartWrapper.getDataTable();
        if (
          selection.length === 0 ||
          // We want to listen to when a whole row is selected. This is the case only when row === null
          selection[0].row ||
          !dataTable
        ) {
          return;
        }
        const columnIndex = selection[0].column;
        const columnID = this.getColumnID(dataTable, columnIndex);
        if (this.state.hiddenColumns.includes(columnID)) {
          this.setState((state) => ({
            ...state,
            hiddenColumns: [
              ...state.hiddenColumns.filter((colID) => colID !== columnID),
            ],
          }));
        } else {
          this.setState((state) => ({
            ...state,
            hiddenColumns: [...state.hiddenColumns, columnID],
          }));
        }
      }
    );
  };

  private applyFormatters = (dataTable: GoogleDataTable, formatters: any[]) => {
    const { google } = this.props;
    for (let formatter of formatters) {
      switch (formatter.type) {
        case "ArrowFormat": {
          const vizFormatter = new google.visualization.ArrowFormat(
            formatter.options
          );
          vizFormatter.format(dataTable, formatter.column);
          break;
        }
        case "BarFormat": {
          const vizFormatter = new google.visualization.BarFormat(
            formatter.options
          );
          vizFormatter.format(dataTable, formatter.column);
          break;
        }
        case "ColorFormat": {
          const vizFormatter = new google.visualization.ColorFormat(
            formatter.options
          );
          const { ranges } = formatter;
          for (let range of ranges) {
            vizFormatter.addRange(...range);
          }
          vizFormatter.format(dataTable, formatter.column);
          break;
        }
        case "DateFormat": {
          const vizFormatter = new google.visualization.DateFormat(
            formatter.options
          );
          vizFormatter.format(dataTable, formatter.column);
          break;
        }
        case "NumberFormat": {
          const vizFormatter = new google.visualization.NumberFormat(
            formatter.options
          );
          vizFormatter.format(dataTable, formatter.column);
          break;
        }
        case "PatternFormat": {
          const vizFormatter = new google.visualization.PatternFormat(
            formatter.options
          );
          vizFormatter.format(dataTable, formatter.column);
          break;
        }
      }
    }
  };
  private getColumnID = (dataTable: GoogleDataTable, columnIndex: number) => {
    return (
      dataTable.getColumnId(columnIndex) ||
      dataTable.getColumnLabel(columnIndex)
    );
  };
  private draw = async ({
    data,
    diffdata,
    rows,
    columns,
    options,
    legend_toggle,
    legendToggle,
    chartType,
    formatters,
    spreadSheetUrl,
    spreadSheetQueryParameters,
  }: ReactGoogleChartProps) => {
    const { google, googleChartWrapper } = this.props;
    let dataTable: GoogleDataTable;
    let chartDiff = null;
    if (diffdata) {
      const oldData = google.visualization.arrayToDataTable(diffdata.old);
      const newData = google.visualization.arrayToDataTable(diffdata.new);
      chartDiff = google.visualization[chartType].prototype.computeDiff(
        oldData,
        newData
      );
    }
    if (data !== null) {
      if (Array.isArray(data)) {
        dataTable = google.visualization.arrayToDataTable(data);
      } else {
        dataTable = new google.visualization.DataTable(data);
      }
    } else if (rows && columns) {
      dataTable = google.visualization.arrayToDataTable([columns, ...rows]);
    } else if (spreadSheetUrl) {
      dataTable = (await loadDataTableFromSpreadSheet(
        google,
        spreadSheetUrl,
        spreadSheetQueryParameters
      )) as GoogleDataTable;
    } else {
      dataTable = google.visualization.arrayToDataTable([]);
    }
    const columnCount = dataTable.getNumberOfColumns();
    for (let i = 0; i < columnCount; i += 1) {
      const columnID = this.getColumnID(dataTable, i);
      if (this.state.hiddenColumns.includes(columnID)) {
        const previousColumnLabel = dataTable.getColumnLabel(i);
        const previousColumnID = dataTable.getColumnId(i);
        const previousColumnType = dataTable.getColumnType(i);
        dataTable.removeColumn(i);
        dataTable.addColumn({
          label: previousColumnLabel,
          id: previousColumnID,
          type: previousColumnType,
        });
      }
    }
    const chart = googleChartWrapper.getChart();
    if (googleChartWrapper.getChartType() === "Timeline") {
      chart && chart.clearChart();
    }
    googleChartWrapper.setChartType(chartType);
    googleChartWrapper.setOptions(options || {});
    googleChartWrapper.setDataTable(dataTable);
    googleChartWrapper.draw();
    if (this.props.googleChartDashboard !== null) {
      this.props.googleChartDashboard.draw(dataTable);
    }

    if (chartDiff) {
      googleChartWrapper.setDataTable(chartDiff);
      googleChartWrapper.draw();
    }
    if (formatters) {
      this.applyFormatters(dataTable, formatters);
      googleChartWrapper.setDataTable(dataTable);
      googleChartWrapper.draw();
    }
    if (legendToggle === true || legend_toggle === true) {
      this.grayOutHiddenColumns({ options });
    }
    return;
  };
  private grayOutHiddenColumns = ({
    options,
  }: {
    options: ReactGoogleChartProps["options"];
  }) => {
    const { googleChartWrapper } = this.props;
    const dataTable = googleChartWrapper.getDataTable();
    if (!dataTable) return;
    const columnCount = dataTable.getNumberOfColumns();
    const hasAHiddenColumn = this.state.hiddenColumns.length > 0;
    if (hasAHiddenColumn === false) return;
    const colors = Array.from({ length: columnCount - 1 }).map(
      (dontcare, i) => {
        const columnID = this.getColumnID(dataTable, i + 1);
        if (this.state.hiddenColumns.includes(columnID)) {
          return GRAY_COLOR;
        } else if (options && options.colors) {
          return options.colors[i];
        } else {
          return DEFAULT_CHART_COLORS[i];
        }
      }
    );
    googleChartWrapper.setOptions({
      ...options,
      colors,
    });
    googleChartWrapper.draw();
  };
  private onResize = () => {
    const { googleChartWrapper } = this.props;
    googleChartWrapper.draw();
  };
  componentDidMount() {
    this.draw(this.props);
    window.addEventListener("resize", this.onResize);
    if (this.props.legend_toggle || this.props.legendToggle) {
      this.listenToLegendToggle();
    }
  }

  componentWillUnmount() {
    const { google, googleChartWrapper } = this.props;
    window.removeEventListener("resize", this.onResize);
    google.visualization.events.removeAllListeners(googleChartWrapper);
    if (googleChartWrapper.getChartType() === "Timeline") {
      googleChartWrapper.getChart() &&
        googleChartWrapper.getChart().clearChart();
    }
  }

  componentDidUpdate() {
    this.draw(this.props);
  }
  render() {
    return null;
  }
}

