                                                                id="from_date"
                                                                value={fromDateValue}
                                                                selected={selectedFromDate}
                                                                className="form-control"
                                                                dateFormat="MMM dd yyyy"
                                                                onChange={(date) => {
                                                                    if (!date) {
                                                                        setFromDateValue("");
                                                                        searchByDateField("from_date", "");
                                                                        return;
                                                                    }
                                                                    searchByDateField("from_date", date);
                                                                    selectedFromDate = date;
                                                                    setSelectedFromDate(date);
                                                                }}
                                                            />
                                                            To:{" "}
                                                            <DatePicker
                                                                id="to_date"
                                                                value={toDateValue}
                                                                selected={selectedToDate}
                                                                className="form-control"
                                                                dateFormat="MMM dd yyyy"
                                                                onChange={(date) => {
                                                                    if (!date) {
                                                                        setToDateValue("");
                                                                        searchByDateField("to_date", "");
                                                                        return;
                                                                    }
                                                                    searchByDateField("to_date", date);
                                                                    selectedToDate = date;
                                                                    setSelectedToDate(date);
                                                                }}
                                                            />
                                                        </span>
                                                    ) : null}
                                                </th>
                                                <th style={{ minWidth: "250px" }}>
                                                    <Typeahead
                                                        id="account_id"
                                                        labelKey="search_label"
                                                        onChange={(selectedItems) => {
                                                            searchByMultipleValuesField(
                                                                "account_id",
                                                                selectedItems
                                                            );
                                                        }}
                                                        options={accountOptions}
                                                        placeholder="Name / mob / acc no."
                                                        selected={selectedAccounts}
                                                        highlightOnlyResult={true}
                                                        onInputChange={(searchTerm, e) => {
                                                            suggestAccounts(searchTerm);
                                                        }}
                                                        multiple
                                                    />
                                                </th>
                                                <th style={{ minWidth: "130px" }}>
                                                    <input
                                                        type="text"
                                                        id="debit"
                                                        onChange={(e) =>
                                                            searchByFieldValue("debit", e.target.value)
                                                        }
                                                        className="form-control"
                                                    />
                                                </th>
                                                <th style={{ minWidth: "130px" }}>
                                                    <input
                                                        type="text"
                                                        id="credit"
                                                        onChange={(e) =>
                                                            searchByFieldValue("credit", e.target.value)
                                                        }
                                                        className="form-control"
                                                    />
                                                </th>
                                                <th >
                                                    <select className="form-control" onChange={(e) =>
                                                        searchByFieldValue("reference_model", e.target.value)
                                                    }>
                                                        <option value="">All</option>
                                                        <option value="sales">Sales</option>
                                                        <option value="sales_return">Sales Return</option>
                                                        <option value="purchase">Purchase</option>
                                                        <option value="purchase_return">Purchase Return</option>
                                                        <option value="capital">Capital</option>
                                                        <option value="customer_deposit">Customer deposit</option>
                                                        <option value="customer_withdrawal">Customer withdrawl</option>
                                                    </select>

                                                </th>
                                                <th style={{ width: "110px" }}>
                                                    <input
                                                        type="text"
                                                        id="reference_code"
                                                        onChange={(e) =>
                                                            searchByFieldValue("reference_code", e.target.value)
                                                        }
                                                        className="form-control"
                                                    />
                                                </th>
