                                                </th>
                                                <th>
                                                    <input
                                                        type="text"
                                                        id="amount"
                                                        onChange={(e) =>
                                                            searchByFieldValue("amount", e.target.value)
                                                        }
                                                        className="form-control"
                                                    />
                                                </th>
                                                <th>
                                                    <input
                                                        type="text"
                                                        id="method"
                                                        onChange={(e) =>
                                                            searchByFieldValue("method", e.target.value)
                                                        }
                                                        className="form-control"
                                                    />
