                                                    <small
                                                        style={{
                                                            color: "blue",
                                                            textDecoration: "underline",
                                                            cursor: "pointer",
                                                        }}
                                                        onClick={(e) =>
                                                            setShowCreatedAtDateRange(!showCreatedAtDateRange)
                                                        }
                                                    >
                                                        {showCreatedAtDateRange ? "Less.." : "More.."}
                                                    </small>
                                                    <br />

                                                    {showCreatedAtDateRange ? (
                                                        <span className="text-left">
                                                            From:{" "}
                                                            <DatePicker
                                                                id="created_at_from"
                                                                value={createdAtFromValue}
                                                                selected={selectedDate}
                                                                className="form-control"
                                                                dateFormat="MMM dd yyyy"
                                                                onChange={(date) => {
                                                                    if (!date) {
                                                                        setCreatedAtFromValue("");
                                                                        searchByDateField("created_at_from", "");
                                                                        return;
                                                                    }
                                                                    searchByDateField("created_at_from", date);
                                                                }}
                                                            />
                                                            To:{" "}
                                                            <DatePicker
                                                                id="created_at_to"
                                                                value={createdAtToValue}
                                                                selected={selectedDate}
                                                                className="form-control"
                                                                dateFormat="MMM dd yyyy"
                                                                onChange={(date) => {
                                                                    if (!date) {
                                                                        setCreatedAtToValue("");
                                                                        searchByDateField("created_at_to", "");
                                                                        return;
                                                                    }
                                                                    searchByDateField("created_at_to", date);
                                                                }}
                                                            />
                                                        </span>
                                                    ) : null}
                                                </th>
                                                <th></th>
                                                <th>
                                                    <select
                                                        onChange={(e) => {
                                                            searchByFieldValue("deleted", e.target.value)
                                                            if (e.target.value == "1") {
                                                                paymentStatus = "none";
                                                                setPaymentStatus(paymentStatus);
                                                                balanceAmount = 0;
                                                                setBalanceAmount(balanceAmount);
                                                            } else {
                                                                paymentStatus = "";
                                                                setPaymentStatus(paymentStatus);
                                                            }
                                                        }}
                                                    >
                                                        <option value="0" SELECTED>NO</option>
                                                        <option value="1">YES</option>
                                                    </select>
                                                </th>
                                            </tr>
                                        </thead>
                                        <tbody className="text-center">
                                            {purchasepaymentList &&
                                                purchasepaymentList.map((purchasepayment) => (
                                                    <tr key={purchasepayment.id}>
                                                        <td>{purchasepayment.purchase_code}</td>
                                                        <td>
                                                            {purchasepayment.date ? format(new Date(purchasepayment.date), "MMM dd yyyy h:mma") : ""}
                                                        </td>
                                                        <td>{purchasepayment.amount.toFixed(2) + " SAR"}</td>
                                                        <td>{purchasepayment.method}</td>
                                                        <td>{purchasepayment.created_by_name}</td>
                                                        <td>
                                                            {format(
                                                                new Date(purchasepayment.created_at),
                                                                "MMM dd yyyy H:mma"
                                                            )}
                                                        </td>
                                                        <td style={{ width: "110px" }}>
                                                            <Button className="btn btn-light btn-sm" disabled={purchasepayment.deleted} onClick={() => {
                                                                openUpdateForm(purchasepayment.id);
                                                            }}>
                                                                <i className="bi bi-pencil"></i>
                                                            </Button>

                                                            <Button className="btn btn-primary btn-sm" disabled={purchasepayment.deleted} onClick={() => {
                                                                openDetailsView(purchasepayment.id);
                                                            }}>
                                                                <i className="bi bi-eye"></i>
                                                            </Button>

                                                            <Button className="btn btn-danger btn-sm" disabled={purchasepayment.deleted} onClick={() => {
                                                                confirmDelete(purchasepayment.id);
                                                            }}>
                                                                <i className="bi bi-trash"></i>
                                                            </Button>

                                                            {/*
                                                        <button
                                                            className="btn btn-outline-secondary dropdown-toggle"
                                                            type="button"
                                                            data-bs-toggle="dropdown"
                                                            aria-expanded="false"
                                                        ></button>
                                                        <ul className="dropdown-menu">
                                                            <li>
                                                                <a href="/" className="dropdown-item">
                                                                    <i className="bi bi-download"></i>
                                                                    Download
                                                                </a>
                                                            </li>
                                                        </ul>
                                                       */}
                                                        </td>
                                                        <td>{purchasepayment.deleted ? "YES" : "NO"}</td>
                                                    </tr>
                                                ))}
                                        </tbody>
                                    </table>
                                </div>

                                {totalPages ? <ReactPaginate
                                    breakLabel="..."
                                    nextLabel="next >"
                                    onPageChange={(event) => {
                                        changePage(event.selected + 1);
                                    }}
                                    pageRangeDisplayed={5}
                                    pageCount={totalPages}
                                    previousLabel="< previous"
                                    renderOnZeroPageCount={null}
                                    className="pagination  flex-wrap"
                                    pageClassName="page-item"
                                    pageLinkClassName="page-link"
                                    activeClassName="active"
                                    previousClassName="page-item"
                                    nextClassName="page-item"
                                    previousLinkClassName="page-link"
                                    nextLinkClassName="page-link"
                                    forcePage={page - 1}
                                /> : ""}
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </>
    );
}

export default PurchasePaymentIndex;
